import mainLogo from './images/mainLogo.svg';
import hamburgerMenu from './images/hamburgerMenu.svg';
import './Header.css';

function Header() {
  const [isOpen, setIsOpen] = useState(false);
  const menuRef = useRef(null);
  const buttonRef = useRef(null);

  const toggleMenu = () => setIsOpen(!isOpen);
  const handleOutsideClick = (ev) => {
    if (menuRef.current && !menuRef.current.contains(ev.target) && !buttonRef.current.contains(ev.target)) {
      setIsOpen(false);
    }
  };

  useEffect(() => {
    document.addEventListener('click', handleOutsideClick);
    return () => document.addEventListener('click', handleOutsideClick);
  }, [])

  return (
    <header-container>
      <img src={mainLogo} className='wanessaLogo' alt="Wanessa logo" />
      <button onClick={toggleHamburguerMenu} className="hamburguer">
        <img src={hamburgerMenu} className='hamburgerMenu' alt="hamburger menu" />
      </button>

      <hamburger-container>
        <button onClick={toggleHamburguerMenu} className="close">
          <img src={hamburgerMenu} className='hamburgerMenu' alt="hamburger menu" />
        </button>
        <a href="#section-id">Home</a>
        <a href="#section-id">Home</a>
      </hamburger-container>
    </header-container>
  );
}

export default Header;
